import weaviate
from loguru import logger
from langflow.custom import Component
from langflow.io import StrInput, IntInput, BoolInput, SecretStrInput, HandleInput, Output
from langflow.schema import Data, Message

class WeaviateVectorStoreComponent(Component):
    display_name = "Weaviate v4 Vector Store"
    description = "Stores and retrieves documents from Weaviate using the v4 API."
    icon = "database"
    name = "WeaviateVectorStore"

    inputs = [
        StrInput(name="host", display_name="Weaviate Host", value="langflow-weaviate", required=True),
        IntInput(name="port", display_name="HTTP Port", value=8080, required=True),
        IntInput(name="grpc_port", display_name="gRPC Port", value=50051, required=True),
        BoolInput(name="is_cloud", display_name="Cloud Connection", value=False, required=True),
        SecretStrInput(name="api_key", display_name="API Key (Cloud Only)", required=False),
        StrInput(name="index_name", display_name="Index Name", required=True, info="Should be capitalized."),
        HandleInput(name="ingest_data", display_name="Ingest Data", input_types=["Data"]),
        HandleInput(name="query", display_name="Search Query", input_types=["Message", "Text"], required=False, info="Text to search in Weaviate."),
        IntInput(name="top_k", display_name="Number of Results", value=5, required=False),
    ]

    outputs = [
        Output(display_name="Connection Status", name="status", method="test_connection"),
        Output(display_name="Stored Document", name="stored_doc", method="store_document"),
        Output(display_name="Search Results", name="search_results", method="search_documents"),
    ]

    def connect_to_weaviate(self):
        """Handles connection to Weaviate v4."""
        try:
            if self.is_cloud and self.api_key:
                logger.info("Connecting to Weaviate Cloud instance...")
                auth_config = weaviate.auth.AuthApiKey(api_key=self.api_key)
                client = weaviate.connect_to_weaviate_cloud(cluster_url=self.host, auth_credentials=auth_config)
            else:
                logger.info("Connecting to local Weaviate instance...")
                client = weaviate.connect_to_local(
                    host=self.host,
                    port=self.port,
                    grpc_port=self.grpc_port,
                    skip_init_checks=True
                )
            return client
        except Exception as e:
            logger.error(f"âŒ Connection error: {e}")
            return None

    def test_connection(self) -> Data:
        """Tests if Weaviate is accessible."""
        client = self.connect_to_weaviate()
        if client and client.is_ready():
            message = f"âœ… Connected to {'Weaviate Cloud' if self.is_cloud else 'Local Weaviate'} successfully."
            logger.info(message)
            return Data(data={"status": "success", "message": message})
        return Data(data={"status": "failed", "message": "âŒ Failed to connect to Weaviate."})

    def store_document(self) -> Data:
        """Stores a document in Weaviate v4 with correct field mapping."""
        client = self.connect_to_weaviate()
        if not client:
            return Data(data={"error": "Failed to connect to Weaviate."})

        if not self.ingest_data or not isinstance(self.ingest_data, Data):
            return Data(data={"error": "No valid document data provided for ingestion."})

        try:
            collection = client.collections.get(self.index_name)
            if not collection:
                logger.info(f"Creating new collection: {self.index_name}")
                client.collections.create(self.index_name, vectorizer="text2vec-transformers")

            document = self.ingest_data.data  # Extracts actual document from Langflow input

            # ðŸ”¹ Ensure "summary" is stored as "content"
            if "summary" in document:
                document["content"] = document.pop("summary")  # Rename key

            response = collection.data.insert(document)
            logger.info(f"âœ… Document stored: {response}")
            return Data(data={"status": "success", "document": response})
        except Exception as e:
            logger.error(f"âŒ Error storing document: {e}")
            return Data(data={"error": str(e)})

    def search_documents(self) -> Data:
        """Performs a similarity search in Weaviate v4."""
        client = self.connect_to_weaviate()
        if not client:
            logger.info("ðŸ” No search query provided. Skipping search_documents execution.")
            return Data(data={})

        # ðŸ” Extract query text depending on input type
        query = self.query
        if isinstance(query, Message):
            query = query.text
        elif isinstance(query, Data):
            query = query.data.get("text", "")
        elif isinstance(query, str):
            query = query.strip()
        else:
            query = ""

        if not query:
            return Data(data={"error": "No valid search query provided."})

        try:
            collection = client.collections.get(self.index_name)
            if not collection:
                return Data(data={"error": f"Collection '{self.index_name}' does not exist."})

            results = collection.query.near_text(query=query, limit=self.top_k)

            docs = []
            for result in results.objects:
                props = result.properties
                props["_id"] = result.uuid
                docs.append(props)

            return Data(data={"results": docs})
        except Exception as e:
            logger.error(f"âŒ Error searching Weaviate: {e}")
            return Data(data={"error": str(e)})
