class MemoryDataModel(BaseModel):
    summary: str = Field(..., description="The extracted memory and extrapolated insights.")
    tags: list[str] = Field(default=[], description="Labels categorizing the memory.")
    importance: int = Field(..., description="A score (1-10) indicating the significance of this memory.")
    timestamp: str = Field(..., description="The timestamp in ISO format (YYYY-MM-DDTHH:MM:SSZ).")
    source: str = Field(..., description="The origin of the memory (e.g., 'chat').")


class LLMJsonExtractorComponent(Component):
    display_name = "LLM JSON Extractor"
    description = "Extracts JSON from LLM responses, even if mixed with markdown or extra text."
    icon = "braces"
    beta = True
    name = "LLMJsonExtractor"

    inputs = [
        MessageInput(
            name="message",
            display_name="LLM Output",
            info="The Message object containing potentially mixed-format JSON output from the LLM.",
        ),
    ]

    outputs = [
        Output(display_name="Parsed Data", name="data", method="extract_and_parse_json"),
    ]

    def extract_json(self, text: str) -> str:
        """Extracts the first valid JSON object from text, handling markdown and extra text."""
        match = re.search(r"```json\s*([\s\S]*?)\s*```", text)
        if match:
            return match.group(1).strip()

        json_match = re.search(r"\{[\s\S]*\}", text)
        if json_match:
            return json_match.group(0).strip()

        return ""

    def extract_and_parse_json(self) -> Data:
        if isinstance(self.message, Message):
            raw_data = self.message.data

            logger.debug("Received LLM output type: {}", type(raw_data))
            logger.debug("Raw LLM output: {}", raw_data)

            if isinstance(raw_data, dict) and "text" in raw_data:
                raw_data = raw_data["text"]

            elif not isinstance(raw_data, str):
                msg = f"Unexpected data format: {type(raw_data)}"
                logger.error("Error: {}", msg)
                return Data(data={"error": msg})

            try:
                json_text = self.extract_json(raw_data)
                if not json_text:
                    raise ValueError("No valid JSON found in LLM output.")

                parsed_data = json.loads(json_text)

                #  Prepare for future conditional termination
                if parsed_data.get("store") == "no":
                    logger.info("Skipping memory storage due to 'store': 'no'")
                    return Data(data={})  # Empty response for termination

                #  Remove 'store' before validating
                parsed_data.pop("store", None)

                memory_data = MemoryDataModel(**parsed_data)

                return Data(data=memory_data.model_dump())

            except (json.JSONDecodeError, ValidationError, ValueError) as e:
                logger.error("Error parsing LLM output JSON: {}", e, exc_info=True)
                return Data(data={"error": str(e)})

        logger.error("Error: Expected a Message object containing JSON.")
        return Data(data={"error": "Expected a Message object containing JSON."})

